codeSnippets.internationalPayment:
// AcumaticaPaymentSerializer.java:138-148
if (isInternationalPayment(payment)) {
    // Use payment calculator for cross-rate
    AcumaticaPaymentCalculator calculator = new AcumaticaPaymentCalculator(
        payment.getCurrencyId(),
        bill.getCurrencyId(),
        exchangeRate
    );

    BigDecimal appliedAmount = calculator.calculateAppliedAmount(payment.getAmount());
    writeDoubleValue("AmountPaid", appliedAmount, jgen);
    writeDoubleValue("CrossRate", calculator.calculateCrossRate(), jgen);
}

codeSnippets.applicationDocuments:
// Link payment to bills
JSONArray applicationDocuments = new JSONArray();
for (Bill bill : payment.getBills()) {
    JSONObject doc = new JSONObject();
    doc.put("DocType", new JSONObject().put("value", "Invoice"));
    doc.put("ReferenceNbr", new JSONObject().put("value", bill.getReferenceNbr()));
    doc.put("AmountPaid", new JSONObject().put("value", bill.getAmountPaid()));
    applicationDocuments.put(doc);
}
root.put("ApplicationDocuments", applicationDocuments);
