// CreateVendorHandler.java:107-127
private static String validateVendorRequest(Map<String, String> raw) {
    if (raw == null) {
        return "Missing vendor data";
    }

    String vendorName = raw.get("vendorName");
    if (StringUtils.isBlank(vendorName)) {
        return "vendorName is required";
    }

    String stampliLink = raw.get("stampliLink");
    if (StringUtils.isBlank(stampliLink)) {
        return "stampliLink is required";
    }

    if (vendorName.length() > 60) {
        return "vendorName exceeds maximum length of 60 characters";
    }

    String vendorId = raw.get("vendorId");
    if (StringUtils.isNotBlank(vendorId) && vendorId.length() > 15) {
        return "vendorId exceeds maximum length of 15 characters";
    }

    return null;  // Validation passed
}

codeSnippets.mapping:
// VendorPayloadMapper.java:16-61
static String mapToAcumaticaJson(Map<String, String> raw, String vendorName, String stampliLink) {
    JSONObject root = new JSONObject();

    // Basic vendor fields
    putValue(root, "VendorName", vendorName);
    putValue(root, "VendorID", raw.get("vendorId"));
    putValue(root, "VendorClass", raw.get("vendorClass"));
    putValue(root, "Terms", raw.get("terms"));
    putValue(root, "CurrencyID", raw.get("currencyId"));

    // Stampli link as note
    JSONObject note = new JSONObject();
    note.put(VALUE_KEY, STAMPLI_LINK_PREFIX + stampliLink.trim());
    root.put("note", note);

    // Payment method and bank instructions
    String paymentMethod = StringUtils.trimToNull(raw.get("paymentMethod"));
    boolean hasBankInfo = StringUtils.isNotBlank(raw.get("bankName")) || 
                         StringUtils.isNotBlank(raw.get("bankAccountNumber")) ||
                         StringUtils.isNotBlank(raw.get("bankRoutingNumber"));

    if (paymentMethod == null && hasBankInfo) {
        paymentMethod = "CHECK";  // Default to CHECK if bank info provided
    }
    putValue(root, "PaymentMethod", paymentMethod);

    // Payment instructions array
    if (hasBankInfo) {
        JSONArray instructions = new JSONArray();
        if (StringUtils.isNotBlank(raw.get("bankName"))) {
            instructions.put(paymentInstruction("BANKNAME", raw.get("bankName")));
        }
        if (StringUtils.isNotBlank(raw.get("bankAccountNumber"))) {
            instructions.put(paymentInstruction("ACCOUNT", raw.get("bankAccountNumber")));
        }
        if (StringUtils.isNotBlank(raw.get("bankRoutingNumber"))) {
            instructions.put(paymentInstruction("ROUTINGNO", raw.get("bankRoutingNumber")));
        }
        root.put("PaymentInstructions", instructions);
    }

    return root.toString();
}

codeSnippets.extractVendorId:
// CreateVendorHandler.java:95-105
private static String extractVendorId(JSONObject obj) {
    if (obj == null) {
        return null;
    }
    // Handle nested structure: {"VendorID": {"value": "V123"}}
    JSONObject idObj = obj.optJSONObject("VendorID");
    if (idObj != null) {
        return idObj.optString("value");
    }
    // Handle flat structure: {"VendorID": "V123"}
    String vendorId = obj.optString("VendorID", null);
    return StringUtils.defaultIfBlank(vendorId, null);
}

